mapper.select:
 		SELECT a.*,b.proName FROM smbms_bill as a
		INNER JOIN smbms_provider b on a.providerId = b.id
		where 1=1
			
				and a.productName LIKE CONCAT('%',#{queryProductName},'%')
			
			
				and a.providerId = #{queryProviderId}
			
			
				and a.isPayment = #{queryIsPayment}
			
			
		 		limit #{from},#{pageSize}
